mapper.select:
SELECT BOARD_PW 
FROM TB_BOARD
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE TB_BOARD SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_NO = #{boardNo}

UPDATE TB_BOARD SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent},
BOARD_WRITER = #{boardWriter},
BOARD_PW = #{boardPw}
WHERE BOARD_NO = #{boardNo}
